                            <path class="cls-1"
                                d="m349.46,155.47l5.33.81c.11.02.21,0,.3-.07.09-.07.14-.15.16-.26l.17-1.1-10.59-1.6,2.52-2.29-3.77-.57.23-1.53,16.18,2.45-.23,1.53-9.65-1.46-1.03.94,9.21,1.39-.52,3.45c-.06.37-.24.66-.56.88-.32.22-.67.3-1.06.24l-8.03-1.22,1.36-1.58Zm-3.89-11.71l14.25,2.16-.46,3.03c-.06.37-.24.66-.55.87-.31.21-.66.29-1.05.23l-12.85-1.94.66-4.34Zm10.8,4.49c.11.02.21,0,.3-.06.09-.06.14-.14.15-.25l.13-.89-8.98-1.36-.19,1.26,8.58,1.3Z" />
