            </svg>
            <div class="absolute main-float w-[65%] h-full top-[54.5%] left-[57%] transform -translate-x-1/2 -translate-y-1/2 rotate-7 select-none">
                <img :src="this.userInfo.avatarUrl" alt="用户头像" @click="handleAvatarClick" v-if="isAuthenticated"
                    class="absolute top-[10%] left-[42%] w-[18%] rounded-full select-none cursor-pointer pointer-events-auto">
                <h1 v-if="show.main && isAuthenticated" class="absolute text-3xl top-1/3 left-1/2 transform -translate-x-1/2 -translate-y-1/2
                w-7/10 h-auto max-h-1/6 flex flex-wrap items-center justify-center text-zinc-300/90 break-words text-center pointer-events-auto overflow-wrap-anywhere">{{ userInfo.signature }}</h1>
                <div v-if="show.info" class="absolute top-[55%] left-1/2
                    transform -translate-x-1/2 -translate-y-1/2 w-full h-1/2 text-gray-200 text-2xl">
                    <p v-text="formData.account" class="absolute top-[6.5%] left-[33%] w-[60%] h-[12%] pointer-events-auto flex items-center"/>
                    <input type="text" v-model="formData.nickname" minlength="0" maxlength="18" title="昵称长度最小两位，最大十八位"
                        class="absolute top-[28.5%] left-[33%] w-[60%] h-[12%] outline-none pointer-events-auto">
                    <input type="text" v-model="formData.signature" minlength="0" maxlength="28" title="签名长度不能超过28位"
                        class="absolute top-[48.4%] left-[32.6%] w-[60%] h-[12%] outline-none pointer-events-auto">
                    <input type="text" v-model="formData.email"
                        class="absolute top-[69.6%] left-[32.8%] w-[60%] h-[12%] outline-none pointer-events-auto">
                </div>
                <div v-if="show.login && !isAuthenticated">
                    <form @submit.prevent="login" class="absolute top-[55%] left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-full h-[40%]
                        text-gray-200 text-2xl">
                        <input type="text" v-model="loginData.account" autocomplete="username" required
                            class="absolute top-[25.5%] left-[34%] w-[60%] h-[15%] transform rotate-2 outline-none pointer-events-auto">
                        <input type="password" v-model="loginData.password" autocomplete="current-password" required
                            class="absolute bottom-[10.2%] left-[33.5%] w-[60%] h-[15%] transform rotate-2 outline-none pointer-events-auto">
                    </form>
                </div>
                <div v-if="show.register && !isAuthenticated">
                    <form @submit.prevent="register"
                        class="absolute w-full h-[65%] top-[15%] rotate-1 text-gray-200 text-2xl">
                        <input type="text" required
                            class="validator w-[60%] h-[7.5%] pointer-events-auto absolute top-[3%] left-[34.5%] rotate-1 outline-none"
                            placeholder="Account" minlength="2" maxlength="45" title="账号长度至少两位"
                            v-model="registerData.account" />
                        <input type="text" required
                            class="validator w-[60%] pointer-events-auto absolute top-[24%] left-[34.7%] rotate-1 outline-none"
                            v-model="registerData.nickname" minlength="2" maxlength="45" placeholder="Nickname"
                            title="昵称长度至少两位" />
                        <input type="email" required
                            class="validator w-[60%] pointer-events-auto absolute top-[45%] left-[34.9%] rotate-1 outline-none"
                            v-model="registerData.email" placeholder="Email" title="请输入有效邮箱地址"
                            autocomplete="new-email" />
                        <input type="password" required
                            class="validator w-[60%] pointer-events-auto absolute top-[67.5%] left-[35%] rotate-1 outline-none"
                            v-model="registerData.password" placeholder="Password" minlength="6" maxlength="45"
                            autocomplete="new-password" title="密码长度至少六位"
                            :class="[{ '!input-error': registerData.confirmPassword && !passwordsMatch }]" />
                        <p v-if="registerData.confirmPassword && !passwordsMatch"
                            class="text-error text-[1.3rem] select-none absolute top-[76.5%] left-[35%]">
                            两次输入的密码不一致
                        </p>
                        <input type="password" required
                            class="validator w-[60%] pointer-events-auto absolute top-[88%] left-[35.2%] rotate-1 outline-none"
                            v-model="registerData.confirmPassword" placeholder="ConfirmPassword" minlength="6"
                            maxlength="45" autocomplete="new-password" title="密码长度至少六位"
                            :class="[{ '!input-error': registerData.confirmPassword && !passwordsMatch }]" />
                        <p v-if="registerData.confirmPassword && !passwordsMatch"
                            class="text-error text-[1.3rem] select-none absolute top-[96.5%] left-[35%]">
                            两次输入的密码不一致
                        </p>
                    </form>
                </div>
            </div>
        </div>
        <input type="file" ref="fileInput" accept=".jpg,.jpeg,.png,.gif,.webp" class="hidden" @change="getAvatarUrl" />
    </div>
</template>

<script>
import { mapGetters } from 'vuex';
import * as service from '../services/userService.js';
import { fire } from '../assets/utils/componentUtils';




export default {
    data() {
        return {
            show: {
                main: true,
                info: false,
                login: true,
                register: false
            },
            loading: {
                updateInfo: false,
                tryRegister: false
            },
            formData: {
                account: '',
                nickname: '',
                signature: '',
                email: '',
                avatarUrl: ''
            },
            loginData: {
                account: '',
                password: ''
            },
            registerData: {
                account: '',
                nickname: '',
                email: '',
                password: '',
                confirmPassword: ''
            }
        };
    },
    computed: {
        ...mapGetters('auth', ['userInfo', 'isAuthenticated']),
        passwordsMatch() {
            return !this.registerData.password || !this.registerData.confirmPassword ||
                this.registerData.password === this.registerData.confirmPassword;
        },
        isFormValid() {
            return this.registerData.account &&
                this.registerData.nickname &&
                this.registerData.email &&
                this.registerData.password &&
                this.registerData.confirmPassword &&
                this.passwordsMatch;
        }
    },
    mounted() {
        this.initFormData();
    },
    methods: {
        switchDisplay(evt) {
            switch (evt) {
                case 'inInfo': {
                    this.show.main = false;
                    this.show.info = true;
                    break;
                }
                case 'outInfo': {
                    this.show.main = true;
                    this.show.info = false;
                    break;
                }
                case 'inRegister': {
                    this.show.register = true;
                    this.show.login = false;
                    break;
                }
                case 'outRegister': {
                    this.show.register = false;
                    this.show.login = true;
                    break;
                }
            }
        },

        initFormData() {
            this.formData = {
                account: this.userInfo.username,
                nickname: this.userInfo.nickname,
                signature: this.userInfo.signature,
                email: this.userInfo.email,
                avatarUrl: this.userInfo.avatarUrl
            }
        },

        async updateUserInfo() {
            if (this.loading.updateInfo) return;
            this.loading.updateInfo = true;
            const { success, error } = await service.updateUserInfo(this.formData);
            if (success) {
                this.initFormData();
                this.message({
                    head: '账户信息更新成功',
                    messages: ['「你是否想过，此刻的名字并非永恒？',
                        '若在暮色将尽时改写墨迹未干的诗行，',
                        '或许会有星子坠入你的眼眸',
                        '——某个古老的机关，总偏爱被晚风掀动的灵魂。」'],
                    target: 'body',
                    time: 20000,
                    allowWrap: false
                });
            } else {
                this.initFormData();
                this.message({
                    head: '账户信息更新失败',
                    messages: [error.head, ...error.messages],
                    target: 'body',
                    time: 4000
                })
                // console.log('更新用户信息失败:', error);
            }
            this.loading.updateInfo = false;
        },

        message(data) {
            this.$emit('message', data);
        },

        close() {
            this.$emit('close');
        },

        logout() {
            this.$emit('logout');
        },

        handleAvatarClick() {
            if (this.show.info) {
                this.$refs.fileInput.click();
            } else {
                this.$emit('showAvatar');
            }
        },

        async getAvatarUrl(event) {
            const file = event.target.files[0];
            if (!file) return;
            const allowedTypes = ['image/jpg', 'image/jpeg', 'image/png', 'image/gif', 'image/webp'];
            if (!allowedTypes.includes(file.type)) {
                this.message({
                    head: '上传失败',
                    messages: ['请选择JPG、JPEG、PNG、GIF或WEBP格式的图片'],
                    target: 'body'
                });
                return;
            }
            if (file.size > 5 * 1024 * 1024) {
                this.message({
                    head: '上传失败',
                    messages: ['文件大小不能超过5MB'],
                    target: 'body'
                });
                return;
            }
            const { success, res, error } = await service.getAvatarUrl();
            // console.log('获取头像上传信息:', res);
            if (success) {
                this.uploadAvatar(res, file);
            } else {
                this.message({
                    head: '获取头像上传信息失败',
                    messages: error,
                    target: 'body'
                })
                // console.log('获取头像上传信息失败：', error);
            }
        },

        async uploadAvatar(res, file) {
            const { success, error } = await service.uploadAvatar(res, file);
            if (success) {
                this.message({
                    head: '上传头像成功',
                    target: 'body'
                });
                const url = {
                    avatarUrl: res.url
                }
                await service.uploadAvatarUrl(url);
            } else {
                this.message({
                    head: '上传头像失败',
                    messages: error,
                    target: 'body'
                })
            }
        },

        login() {
            if (!this.loginData.account || !this.loginData.password) {
                this.message({
                    head: '登录失败',
                    messages: ['请输入账号和密码'],
                    target: 'body',
                });
                return;
            }
            const data = {
                login: this.loginData.account,
                password: this.loginData.password
            }
            const callback = (success) => {
            if (success) {
                this.initFormData();
                this.registerData = {
                    account: '',
                    nickname: '',
                    email: '',
                    password: '',
                    confirmPassword: ''
                }
                this.loginData = {
                    account: '',
                    password: ''
                }
            }
            }
            this.$emit('login', data, callback);
        },

        async register() {
            if (!this.isFormValid) {
                return;
            }
            if (this.loading.tryRegister) {
                return
            };
            this.loading.tryRegister = true;
            const registerData = {
                nickname: this.registerData.nickname,
                email: this.registerData.email,
                username: this.registerData.account,
                password: this.registerData.password,
            };
            const { success, messages } = await service.register(registerData);
            if (success) {
                this.firework();
                this.loginData.account = registerData.username || registerData.email;
                this.loginData.password = registerData.password;
                this.login();
            } else {
                const data = {
                    head: '注册失败：',
                    messages,
                    target: 'body',
                    time: 4000
                }
                this.$emit('message', data);
            }
            this.loading.tryRegister = false;
        },

        firework() {
            const origin = { y: 1.05, x: 0.5 };
            fire(0.25, {
                spread: 66,
                startVelocity: 75,
                scalar: 0.8
            }, origin);
            fire(0.2, {
                spread: 60
            }, origin);
            fire(0.35, {
                spread: 100,
                decay: 0.91
            }, origin);
            fire(0.3, {
                spread: 130,
                startVelocity: 66,
                decay: 0.92,
                scalar: 1.2
            }, origin);
            fire(0.4, {
                spread: 120,
                startVelocity: 45
            }, origin);
        },
    },
}
</script>

